const EmotionsGame = ({ onComplete }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [score, setScore] = useState(0)
  const [selectedOption, setSelectedOption] = useState(null)

  const questions = [
    {
      image: "/placeholder.svg?height=200&width=200",
      question: "Como esta pessoa estÃ¡ se sentindo?",
      options: ["Feliz", "Triste", "Com raiva", "Surpreso"],
      answer: "Feliz",
    },
    {
      image: "/placeholder.svg?height=200&width=200",
      question: "Qual emoÃ§Ã£o esta expressÃ£o representa?",
      options: ["Medo", "Tristeza", "Alegria", "Nojo"],
      answer: "Tristeza",
    },
    {
      image: "/placeholder.svg?height=200&width=200",
      question: "O que esta pessoa estÃ¡ sentindo?",
      options: ["ConfusÃ£o", "Surpresa", "Raiva", "Vergonha"],
      answer: "Surpresa",
    },
    {
      image: "/placeholder.svg?height=200&width=200",
      question: "Identifique a emoÃ§Ã£o mostrada:",
      options: ["Raiva", "Medo", "Ansiedade", "Entusiasmo"],
      answer: "Raiva",
    },
    {
      image: "/placeholder.svg?height=200&width=200",
      question: "Qual sentimento estÃ¡ sendo demonstrado?",
      options: ["Orgulho", "Curiosidade", "Medo", "TÃ©dio"],
      answer: "Medo",
    },
  ]

  const handleAnswer = (option) => {
    setSelectedOption(option)

    setTimeout(() => {
      if (option === questions[currentQuestion].answer) {
        setScore(score + 1)
      }

      const nextQuestion = currentQuestion + 1

      if (nextQuestion < questions.length) {
        setCurrentQuestion(nextQuestion)
        setSelectedOption(null)
      } else {
        const finalScore = Math.round(
          ((score + (option === questions[currentQuestion].answer ? 1 : 0)) / questions.length) * 100,
        )
        onComplete(finalScore)
      }
    }, 1000)
  }

  return (
    <div className="max-w-3xl mx-auto">
      <div className="mb-4 flex justify-between items-center">
        <span className="text-sm font-medium text-gray-500">
          QuestÃ£o {currentQuestion + 1} de {questions.length}
        </span>
        <span className="text-sm font-medium text-gray-500">PontuaÃ§Ã£o: {score}</span>
      </div>

      <div className="bg-gray-50 p-6 rounded-lg mb-6">
        <h4 className="text-lg font-medium text-gray-900 mb-4">{questions[currentQuestion].question}</h4>
        <div className="flex justify-center mb-6">
          <img
            src={questions[currentQuestion].image || "/placeholder.svg"}
            alt="ExpressÃ£o facial"
            className="w-48 h-48 object-cover rounded-lg"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          {questions[currentQuestion].options.map((option) => (
            <button
              key={option}
              onClick={() => handleAnswer(option)}
              disabled={selectedOption !== null}
              className={`
                py-3 px-4 rounded-md text-sm font-medium
                ${
                  selectedOption === option
                    ? option === questions[currentQuestion].answer
                      ? "bg-green-100 text-green-800 border-green-300"
                      : "bg-red-100 text-red-800 border-red-300"
                    : "bg-white border border-gray-300 hover:bg-gray-50"
                }
                ${selectedOption !== null && option !== selectedOption ? "opacity-50" : ""}
                focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500
              `}
            >
              {option}
            </button>
          ))}
        </div>
      </div>

      <div className="w-full bg-gray-200 rounded-full h-2.5">
        <div
          className="bg-indigo-600 h-2.5 rounded-full"
          style={{ width: `${(currentQuestion / questions.length) * 100}%` }}
        ></div>
      </div>
    </div>
  )
}

